mapper.select:
select * from products


        category = #{product.category}


        and id = #{product.id}


        and name like '%' #{product.name} '%'


        and price > #{minprice}


        and price < #{maxprice}

select products.name, SUM(orderitem.buynum) salnum from orders, products, orderitem
orders.paystate = 1 and orderitem.order_id = orders.id and products.id = orderitem.product_id and year(orders.ordertime) = #{year}
and month(orders.ordertime) = #{month}
group by products.id order by salnum desc
